import {FETCH_POSTS,NEW_POST} from '../actions/type';
const initialState = {
    items: [],
    item: {}
}
export default function(state = initialState,action){
    console.log(action);
    switch(action.type){
        case FETCH_POSTS:
            return {
                ...state,
                items: action.payload
            }
        default:
            return state;
            
    }
}
