import {FETCH_POSTS,NEW_POST} from './type';
export const fetchPosts = ()=>dispatch=>{
        fetch('https://jsonplaceholder.typicode.com/posts')
        .then(res=>res.json())
        .then(posts =>{
            console.log(posts);
            return dispatch({
            type: FETCH_POSTS,
            payload:posts
        })})
}
