import {FETCH_DATA} from '../utilities/config';
const userReducer = (state = {
    "id": "111",
    "first_name": "Doyle",
    "last_name": "Lind",
    "gender": "male",
    "dob": "2015-05-23",
    "email": "zemlak.friedrich@example.org",
    "phone": "987-995-6143 x3312",
    "website": "https://west.org/qui-et-ducimus-asperiores-sint-quia-tempora.html",
    "address": "797 Block Drive\nNew Newellchester, AR 84320-8505",
    "status": "active",
    "_links": {
      "self": {
        "href": "https://gorest.co.in/public-api/users/111"
      },
      "edit": {
        "href": "https://gorest.co.in/public-api/users/111"
      },
      "avatar": {
        "href": "https://lorempixel.com/250/250/people/?61728"
      }
    }
  },action)=>{
    switch(action.type){
        case FETCH_DATA:
            return {
                user: action.payload
            }
        default:
            return state;
    }
}
export default userReducer;
