import {FETCH_DATA} from '../utilities/config';
const userReducer = (state = {users:[]},action)=>
{
    console.log(action);
    switch(action.type)
    {
        case FETCH_DATA:
            return {
                users : action.payload
            }
        default:
            return state;
    }
}
export default userReducer;
