        method: 'GET',
        headers: 
        {
            'content-type':'application/json',
            'Authorization':'Bearer lRfeu-VLiV5HB-qG6ZoI9c67XwjIeR0GwK3_'
        }
    }).then(res =>res.json())
    .then(data =>
        {
            console.log("data");
            console.log(data);
        })
    }
    render() { 
        return ( 
        <div className="jumbotron container">
            {this.props.data.map(user =>(<User user = {user}/>))}
        </div> );
    }
}
